con:config.script:
// define properties required for the script to run.
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectPath = groovyUtils.projectPath
def dataFolder = projectPath + "/testData"
def soapRequestStep = testRunner.testCase.getTestStepByName("CelsiusToFahrenheit_Positive")
//Define an empty array list to load data from datasheet
def DataTable = [];
// Read data file using InputStreamReader
File datafile = new File(dataFolder+"/TestDataSheet_Positive.csv"); 
//We are using the row count to exclude header record
int currentRow= 1;
if(datafile.getName().equalsIgnoreCase("TestDataSheet_Positive.csv")) {
 inputStream = new FileInputStream(datafile)
 dataReader = new BufferedReader(new InputStreamReader(inputStream))
 // Read records line by line
 dataReader.eachLine{
 singleLine->  DataTable.add( singleLine.split(","))

}
}



// Exclude header record
int totalRecords = (DataTable.size()-1)
log.info "Loaded "+totalRecords +" records"

// Store the data in soapui test context
context.setProperty( "DataTable", DataTable )
context.setProperty( "TotalRecords", totalRecords )
context.setProperty( "currentRow", currentRow )

def dataSet=testRunner.testCase.getTestStepByName("Properties")
def DataTable = context.getProperty("DataTable") // Retrieve the test data from the testData property
def currentRow = context.getProperty("currentRow") // Track which line of the test input file we're on
  // Select the current test data line


    def singleLineArray = DataTable[currentRow]
    //Set  data of the second column of current line into Celsius_Input property
	dataSet.setPropertyValue( "Celsius_Input", singleLineArray[1] )
	//Set data of the third column of current line into Celsius_Input property
	dataSet.setPropertyValue( "Expected_Output", singleLineArray[2] )

log.info "running data row " +currentRow

int currentRow = context.getProperty("currentRow")
int totalRecords =context.getProperty("TotalRecords")

// Go back to "SetData" for running the next record . Else exit

if(currentRow<totalRecords){
currentRow=currentRow+1
context.setProperty( "currentRow", currentRow )
testRunner.gotoStepByName("SetData")
log.info " loop back for data row " + currentRow

}
else
{
	log.info "Completed"
}

